package org.foobarspam.ejercicio16;

public class MediaAritmetica {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int total = 0;
		for (int i=0; i<31; i++){
			System.out.println(i);
			total = total + i;
			
		}
		System.out.println("La media de aritmética de el 1 al 30 es " + (total/30));
	}

}
